import socket

# 宛先のIPアドレスとポート番号
ip = input("Enter the IP address: ")
port = int(input("Enter the port number: "))

# UDPソケットの作成
sock = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)

# 送るメッセージ
message = b""

# パケットの送信
try:
    while True:
        sock.sendto(message, (ip, port))
except KeyboardInterrupt:
    print("送信を終了します。")
finally:
    sock.close()